<?php

namespace Plugin\ProductOptions\Repository;

use Doctrine\Persistence\ManagerRegistry as RegistryInterface;
use Eccube\Doctrine\Query\Queries;
use Eccube\Repository\AbstractRepository;
use Plugin\ProductOptions\Entity\OrderOptions;

/**
 * OrderOptionsRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class OrderOptionsRepository extends AbstractRepository
{
    /**
     * @var Queries
     */
    protected $queries;

    public const COLUMNS = [
        'id'            => 'd.id',
        'order_id'      => 'd.order_id',
        'option_id'     => 'd.option_id',
    ];

    /**
     * OrderOptionsRepository constructor.
     *
     * @param RegistryInterface $registry
     */
    public function __construct(RegistryInterface $registry, Queries $queries)
    {
        parent::__construct($registry, OrderOptions::class);

        $this->queries = $queries;
    }
}
